    public boolean hasPath(String start, String end) {
        PriorityQueue<Edge> queue = new PriorityQueue<>();

        Set<String> visited = new HashSet<>();
        Map<String, Integer> distances = new HashMap<>();

        distances.put(start, 0);
        queue.offer(new Edge(start, start, 0));

        while (!queue.isEmpty()) {
            Edge currentEdge = queue.poll();

            if (currentEdge.destination == end && !visited.contains(end)) {
                return true;
            } else if (!visited.contains(currentEdge.destination)) {
                visited.add(currentEdge.destination);

