// Load the AI model
let aiModel; 
fetch("ai_model.json")
  .then(response => response.json())
  .then(model => {
    aiModel = model;
  });

// Listen for messages from content scripts 
chrome.runtime.onMessage.addListener(function(request, sender, sendResponse) {
  if (request.message === "generateReply") {
    // Generate AI reply using the loaded model
    const reply = aiModel.generateReply(request.emailSubject, request.emailBody);

    // Send the reply back to the content script
    sendResponse({reply: reply});
  }
});
