```js
// Listen for clicks on the reply button 
document.addEventListener("click", function(e) {
  if (e.target.textContent === "Reply" || e.target.textContent === "Reply all") {
    // Get the subject and body of the email
    const emailSubject = document.querySelector(".subject").textContent;
    const emailBody = document.querySelector(".body").textContent;

    // Send a message to the background script to generate a reply
    chrome.runtime.sendMessage({
      message: "generateReply", 
      emailSubject: emailSubject,
      emailBody: emailBody
    }, function(response) {
      // Copy the reply to the clipboard and click the reply button
      navigator.clipboard.writeText(response.reply);
      e.target.click();
    });
  }
});
